mapper.select:

select 

from ptreadypackagefail
where TerminalNo = #{terminalno,jdbcType=VARCHAR}
  and PackageID = #{packageid,jdbcType=VARCHAR}

mapper.delete:

delete from ptreadypackagefail
where TerminalNo = #{terminalno,jdbcType=VARCHAR}
  and PackageID = #{packageid,jdbcType=VARCHAR}
