mapper.select:

select 

from storeinrecord
where terminalID = #{terminalid,jdbcType=VARCHAR}
  and boxID = #{boxid,jdbcType=INTEGER}
  and userCardID = #{usercardid,jdbcType=VARCHAR}
  and storeInTime = #{storeintime,jdbcType=TIMESTAMP}

mapper.delete:

delete from storeinrecord
where terminalID = #{terminalid,jdbcType=VARCHAR}
  and boxID = #{boxid,jdbcType=INTEGER}
  and userCardID = #{usercardid,jdbcType=VARCHAR}
  and storeInTime = #{storeintime,jdbcType=TIMESTAMP}
